mapper.select:
select

from oa_file_manage


    `create_date` <= DATE_ADD(#{createDate, jdbcType=TIMESTAMP},INTERVAL 1 MONTH ) AND `create_date` >= #{createDate, jdbcType=TIMESTAMP}


    and create_user = #{userId,jdbcType=BIGINT}


    and type = #{type,jdbcType=VARCHAR}


    and part_name = #{partName,jdbcType=VARCHAR}


order by create_date desc
